// Esse reducer será responsável por tratar as informações da pessoa usuária
//* aqui seta o estado inicial para essas infos

const INITIAL_STATE = {
  email: '',
  senha: '',
};
export default function userReducer(state = INITIAL_STATE, action) {
  switch (action.type) {
  case SAVE_INPUTS:
    return {
      ...state,
    };
  default:
    return state;
  }
}
