import React from 'react';
import { connect } from 'react-redux';

class Form extends React.Component {
  render() {
    const { valueInput, descriptionInput } = this.setState;
    return (
      <form>
        <label htmlFor="value-input">
          Valor da despesa:
          {' '}
          <input
            data-testid="value-input"
            id="value-input"
            type="number"
            value={ valueInput }
          />
        </label>
        <label htmlFor="description-input">
          Descrição:
          {' '}
          <input
            data-testid="description-input"
            id="description-input"
            type="textArea"
            value={ descriptionInput }
          />
        </label>
        <label htmlFor="moeda">
          Moeda:
          {' '}
          <select
            name="moeda"
            id="moeda"
            value={ moeda }
          >
            {' '}
            { alo }
          </select>
        </label>
      </form>
    );
  }
}
const mapStateToProps = (dispatch) => ({ // para ler estados
  addInputs: (input) => dispatch(saveInputs(input)),
});
export default connect(null, mapStateToProps)(Form);
