import React from 'react';
// import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      senha: '',
      sendButton: true,
    };
  }

  onChangeInput = ({ target }) => { // pegar o value dos inputs de forma dinamica
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  // Botao comeÃ§a desativado e precisa ativar somente quando
  // estiver com email no formato valido e senha de no min 6S
  checkInputs = () => {
    const { email, senha } = this.state;
    const numCaractMin = 6;
    const emailTest = "aloo@email.com";
    const reg = /^\w+([-+.']\w+)*@\w+([-.]\w+)*\.\w+([-.]\w+)*$/;
    if (senha.length >= numCaractMin && email.value.include('@')) {
      this.setState({ sendButton: false });
    }
  }

  render() {
    const { email,
      senha,
      handleButton,
      sendButton,
    } = this.state;
    return (
      <form>
        <label htmlFor="email">
          Email:
          {' '}
          <input
            id="email"
            type="email"
            name="email"
            value={ email }
            onChange={ this.onChangeInput }
            data-testid="email-input"
          />
        </label>
        <label htmlFor="senha">
          Senha:
          {' '}
          <input
            id="senha"
            type="password"
            name="senha"
            value={ senha }
            onChange={ this.onChangeInput }
            data-testid="password-input"
          />
        </label>
        <Link to="Wallet">
          <button
            type="submit"
            onClick={ handleButton }
            disabled={ sendButton }
          >
            Entrar
          </button>
        </Link>
      </form>
    );
  }
}
