// Coloque aqui suas actions
//* aqui que irei salvar os inputs e moedas trazidas pela API.
//* uma action para salvar os inputs e outra para mostrar role?

export const SAVE_INPUTS = 'SAVE_INPUTS';
export const SAVE_CURRENCIES = 'SAVE_CURRENCIES';
export const SAVE_EXPENSES = 'SAVE_EXPENSES';

export function saveInputs(payload) {
  return {
    type: SAVE_INPUTS,
    payload,
  };
}

export function saveCurrencies(payload) {
  return {
    type: SAVE_CURRENCIES,
    payload,
  };
}

export function saveExpenses(payload) {
  return {
    type: SAVE_EXPENSES,
    payload,
  };
}
