import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {
      despesaTotal: 0,
      moeda: 'BRL',
    };
  }

  render() {
    const { user } = this.props;
    const { despesaTotal, moeda } = this.state;
    return (
      <header>
        <h1>TrybeWallet</h1>
        { user.map(({ email }) => (
          <h3 data-testid="email-field" key={ email }>{ email }</h3>
        ))}
        <h3 data-testid="total-field">
          {' '}
          Despesa total de gastos:
          {' '}
          {despesaTotal}
        </h3>
        <h3 data-testid="header-currency-field"> {moeda} </h3>
      </header>
    );
  }
}
const mapStateToProps = (state) => ({
  user: state.user,
});
export default connect(mapStateToProps)(Wallet);

Wallet.propTypes = {
  user: PropTypes.arrayOf(PropTypes.shape({})).isRequired,
};
