import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { saveExpenses } from '../actions';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      cliclou: false,
    };
  }

  handleChange = ({ target }) => { // pegar o value dos inputs de forma dinamica
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { addExpenses } = this.props;
    addExpenses(this.state);
  }

  addDespesa = (event) => {

  };

  render() {
    const { valueInput, descriptionInput, moeda, methodInput, tagInput } = this.setState;
    const { currencies } = this.props;
    return (
      <form onSubmit={ this.handleSubmit }>
        <label htmlFor="value-input">
          Valor da despesa:
          {' '}
          <input
            data-testid="value-input"
            id="value-input"
            type="number"
            value={ valueInput }
          />
        </label>
        <label htmlFor="description-input">
          Descrição:
          {' '}
          <input
            data-testid="description-input"
            id="description-input"
            type="textArea"
            value={ descriptionInput }
          />
        </label>
        <label htmlFor="moeda">
          Moeda:
          {' '}
          <select
            name="moeda"
            id="moeda"
            value={ moeda }
            onChange={ this.handleChange }
          >
            {currencies.map((coins) => (<option key={ coins }>{ coins }</option>))}
          </select>
        </label>
        <label htmlFor="method-input">
          Método de pagamento:
          {' '}
          <select
            data-testid="method-input"
            name="method-input"
            id="method-input"
            value={ methodInput }
            onChange={ this.handleChange }
          >
            <option>Dinheiro</option>
            <option>Cartão de crédito</option>
            <option>Cartão de débito</option>
          </select>
        </label>
        <label htmlFor="tag-input">
          Método de pagamento:
          {' '}
          <select
            data-testid="tag-input"
            name="tag-input"
            id="tag-input"
            value={ tagInput }
            onChange={ this.handleChange }
          >
            <option>Alimentação</option>
            <option>Lazer</option>
            <option>Trabalho</option>
            <option>Transporte</option>
            <option>Saúde</option>
          </select>
        </label>
        <button
          type="submit"
          onClick={ this.addDespesa }
        >
          Adicionar despesa
        </button>
      </form>
    );
  }
}
const mapStateToProps = (globalState) => ({
  currencies: globalState.wallet.currencies,
});
const mapDispatchToProps = (dispatch) => ({ // para ler estados
  addExpenses: (input) => dispatch(saveExpenses(input)),
});

Form.propTypes = {
  currencies: PropTypes.arrayOf.isRequired,
  addExpenses: PropTypes.func.isRequired,
};
export default connect(mapStateToProps, mapDispatchToProps)(Form);
