import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { saveCurrencies } from '../actions';

class Wallet extends React.Component {
  constructor() {
    super();
    this.state = {
      despesaTotal: 0,
      currencies: 'BRL',
    };
  }

  componentDidMount() {
    const { dispatch } = this.props;
    const currenciesFilter = Object.keys(currencies).filter((coin) => coin !== 'USDT');
    fetch('https://economia.awesomeapi.com.br/json/all')
      .then((response) => response.json())
      .then((currencies) => dispatch(saveCurrencies(currenciesFilter)));
  }

  render() {
    const { email } = this.props;
    const { despesaTotal, currencies } = this.state;
    return (
      <header>
        <h1>TrybeWallet</h1>
        <h3 data-testid="email-field">{ email }</h3>
        <h3 data-testid="total-field">
          {' '}
          Despesa total de gastos:
          {' '}
          {despesaTotal}
        </h3>
        <h3 data-testid="header-currency-field">
          {' '}
          {currencies}
          {' '}
        </h3>
      </header>
    );
  }
}
const mapStateToProps = (state) => ({
  email: state.user.email,
});
export default connect(mapStateToProps)(Wallet);

Wallet.propTypes = {
  email: PropTypes.string.isRequired,
  dispatch: PropTypes.func.isRequired,
};
