import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { saveExpenses } from '../actions';

class Form extends React.Component {
  constructor() {
    super();
    this.state = {
      id: 0,
      value: '',
      description: '',
      currency: '',
      method: 'Dinheiro',
      tag: 'Alimentação',
      cliclou: false,
    };
  }

  handleChange = ({ target }) => { // pegar o value dos inputs de forma dinamica
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    const { addExpenses } = this.props;
    addExpenses(this.state);
  }

  // addDespesa = (event) => {
  //   const { dispatch } = this.props;
  //   fetch('https://economia.awesomeapi.com.br/json/all')
  //     .then((response) => response.json());
  // };

  render() {
    // const { descriptionInput, moeda, methodInput, tagInput } = this.state;
    const { currencies } = this.props;
    return (
      <form onSubmit={ this.handleSubmit }>
        <label htmlFor="value-input">
          Valor da despesa:
          {' '}
          <input
            data-testid="value-input"
            id="value-input"
            name="value"
            type="number"
            // value={ valueInput }
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="description-input">
          Descrição:
          {' '}
          <input
            data-testid="description-input"
            id="description-input"
            type="textArea"
            name="description"
            value={ descriptionInput }
            onChange={ this.handleChange }
          />
        </label>
        <label htmlFor="moeda">
          Moeda:
          {' '}
          <select
            name="moeda"
            id="moeda"
            value={ moeda }
            onChange={ this.handleChange }
          >
            {currencies.map((coins) => (
              <option key={ coins } value={ coins }>{ coins }</option>))}
          </select>
        </label>
        <label htmlFor="method-input">
          Método de pagamento:
          {' '}
          <select
            data-testid="method-input"
            name="method"
            id="method-input"
            value={ methodInput }
            onChange={ this.handleChange }
          >
            <option value="Dinheiro">Dinheiro</option>
            <option value="Cartão de crédito">Cartão de crédito</option>
            <option value="Cartão de débito">Cartão de débito</option>
          </select>
        </label>
        <label htmlFor="tag-input">
          Método de pagamento:
          {' '}
          <select
            data-testid="tag-input"
            name="tag"
            id="tag-input"
            value={ tagInput }
            onChange={ this.handleChange }
          >
            <option value="Alimentação">Alimentação</option>
            <option value="Lazer">Lazer</option>
            <option value="Trabalho">Trabalho</option>
            <option value="Transporte">Transporte</option>
            <option value="Saúde">Saúde</option>
          </select>
        </label>
        <button
          type="submit"
          onClick={ this.addDespesa }
        >
          Adicionar despesa
        </button>
      </form>
    );
  }
}
const mapStateToProps = (globalState) => ({
  currencies: globalState.wallet.currencies,
});
const mapDispatchToProps = (dispatch) => ({ // para ler estados
  addExpenses: (input) => dispatch(saveExpenses(input)),
});

Form.propTypes = {
  currencies: PropTypes.arrayOf.isRequired,
  addExpenses: PropTypes.func.isRequired,
};
export default connect(mapStateToProps, mapDispatchToProps)(Form);
