import React from 'react';
// import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      email: '',
      senha: '',
      SendButton: true,
    };
    this.onChangeInput = this.onChangeInput.bind(this);
    this.checkInputs = this.checkInputs.bind(this);
  }

  onChangeInput({ target }) { // pegar o value dos inputs de forma dinamica
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  checkInputs() {
    const { email, senha } = this.state;
    const numCaractMin = 6;
    // const emailFormat = /^[a-z0-9.]+@[a-z0-9]+\.[a-z]+\.([a-z]+)?$/i;
    if (senha.length >= numCaractMin && email.value.include('@')) {
      this.setState({ SendButton: false });
    }
  }

  render() {
    const { email,
      senha,
      handleButton,
      SendButton,
    } = this.state;
    return (
      <form>
        <label htmlFor="email">
          Email:
          {' '}
          <input
            id="email"
            type="email"
            name="email"
            value={ email }
            onChange={ this.onChangeInput }
            data-testid="email-input"
          />
        </label>
        <label htmlFor="senha">
          Senha:
          {' '}
          <input
            id="senha"
            type="password"
            name="senha"
            value={ senha }
            onChange={ this.onChangeInput }
            data-testid="password-input"
          />
        </label>
        <Link to="Wallet">
          <button
            type="submit"
            onClick={ handleButton }
            disabled={ SendButton }
          >
            Entrar
          </button>
        </Link>
      </form>
    );
  }
}
// Login.propTypes = {
